package com.thinksns.unit;import com.thinksns.android.R;import com.thinksns.android.ThinksnsCreate;import android.app.Activity;import android.widget.EditText;public class AtHelper {  private Activity activity;  private String strDefaultAt = "";  private EditText editText;  public AtHelper(ThinksnsCreate paramEditorActivity)  {    this.activity = paramEditorActivity;    editText = paramEditorActivity.edit;    this.strDefaultAt = this.activity.getString(R.string.input_text_at);  }  public AtHelper(Activity paramEditorActivity , EditText editText)  {    this.activity = paramEditorActivity ;    this.editText = editText ;    this.strDefaultAt = this.activity.getString(R.string.input_text_at);  }    public void insertAtTips()  {	EditText  localEditBlogView = this.editText;    int i = localEditBlogView.getSelectionStart();    int j = localEditBlogView.getSelectionEnd();    localEditBlogView.getText().replace(i, j, this.strDefaultAt + " ");    localEditBlogView.setSelection(i + 1, i + this.strDefaultAt.length());  }}